package Misc;

public class CharacterReplacement {
    public static void main(String[] args) {
        String s = "";
//        System.out.println(s.length());
        System.out.println(characterReplacement(s, 1769));
    }

    public static int characterReplacement(String s, int k) {

        char ch = 'A';
        int max = 0;
        while (ch <= 'Z') {
            int d = 0;
            for (int i = 0; i < s.length(); i++) {
                char y = s.charAt(i);
                if (y == ch) {
                    d++;
                    continue;
                }
                int slide = 0;
                for (int j = i; j < s.length(); j++) {
                    char x = s.charAt(j);
                    if (x != ch) {
                        slide++;
                        if (slide > k)
                            break;
                    }
                    d++;
                }
                if (d > max)
                    max = d;
                d = 0;
            }
            if (d > max)
                max = d;
            ch++;
        }

        return max;
    }
}

